import React, { Component } from "react";
import "./searcher.css";
const job_type_items = ["Engineering", "Design", "Marketing", "IT", "Business"];
class Searcher extends Component {
  constructor(props) {
    super(props);
    this.state = { isToggleOn: false };

    this.handleClick = this.handleClick.bind(this);
  }
  handleClick() {
    this.setState((state) => ({
      isToggleOn: !state.isToggleOn,
    }));
  }
  render() {
    return (
      <section className="searcher_container">
        <div className="left_filter">
          <section className="location_filter">
            <p>Location</p>
          </section>
          <section className="job_type">
            <p>Job Type</p>
            <ul>
              {job_type_items.map((a, index) => {
                return <li key={index}>{a}</li>;
              })}
            </ul>
          </section>

          <section className="english_level">
            <p>English Level</p>
            <ul>
              <li>Not required</li>
              <li>Fluent</li>
              <li>Conversational</li>
              <li>Business Level</li>
            </ul>
          </section>
        </div>
        <MyForm />
      </section>
    );
  }
}
class MyForm extends Component {
  render() {
    return (
      <section>
        <form>
          <input></input>
        </form>
      </section>
    );
  }
}
export default Searcher;
